import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { isEmail } from "validator";
import AuthService from "../services/auth.service";
import './Login.css';

function Login({ show, close }) {

    let history = useHistory();

    const [toggler, setToggler] = useState(false);

    const [values, setValues] = useState({
        full_name: '',
        email: '',
        password: '',
        password2: ''
    });

    const [message, setMessage] = useState("");

    const [successful, setSuccessful] = useState(false);

    const [errors, setErrors] = useState({});

    const [isSubmitting, setIsSubmitting] = useState(false);

    const [isSubmitted, setIsSubmitted] = useState(false);

    function submitForm() {
        setIsSubmitted(true);
    }

    function validate(values) {
        let errors = {};

        if (!values.full_name.trim()) {
            errors.full_name = "Full name required";
        }

        if (!values.email) {
            errors.email = "Email required";
        } else if (!isEmail(values.email)) {
            errors.email = "Email address is invalid";
        }

        if (!values.password) {
            errors.password = 'Password is required';
        } else if (values.password.length < 6 || values.password.length > 50) {
            errors.password = "Password must be between 6 and 50 characters ";
        }

        if (!values.password2) {
            errors.password2 = "Password confirmation is required";
        } else if (values.password2 !== values.password) {
            errors.password2 = "Password do no match";
        }

        return errors;
    }

    const handleChange = e => {
        const { name, value } = e.target;
        setValues({
            ...values,
            [name]: value
        });
    };


    function handleRegister(e) {
        e.preventDefault();

        setErrors(validate(values));
        setIsSubmitting(true);

        AuthService.register(
            values.full_name,
            values.email,
            values.password
        ).then(
            response => {
                setMessage(response.data.message);
            },
            error => {
                const resMessage = (
                    error.response &&
                    error.response.data &&
                    error.response.data.message) || error.message || error.toString();
                setMessage(resMessage);
                setSuccessful(false);
            }
        );

    }



function handleLogin(e) {
    e.preventDefault();

  
    AuthService.login(values.email, values.password).then(
        () => {
            const currentUserRole = AuthService.getCurrentUser().role;
            if (currentUserRole === "ROLE_USER") {
                history.push("/user");
                window.location.reload();
            } else if (currentUserRole === "ROLE_ADMIN") {
                history.push("/admin");
                window.location.reload();
            }

        },
        error => {
            const resMessage =
                (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                error.message ||
                error.toString();

            setMessage(resMessage);
        }
    )
}

useEffect(
    () => {
        if (Object.keys(errors).length === 0 && isSubmitting) {
            submitForm();
        }
    },
    [errors]
);

return (
    <div>
        <div className="back-drop" style={{
            transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
        }} onClick={close} />

        <div className="login" style={{
            display: toggler && 'none'
        }}>
            <h1>Login</h1>
            <form onSubmit={handleLogin} noValidate>
                <div className="txt_field">
                    <input
                        type="text"
                        name="email"
                        value={values.email}
                        onChange={handleChange}
                    />
                    <label>Email</label>
                    <span></span>
                    {errors.email && <p>{errors.email}</p>}
                </div>
                <div className="txt_field">
                    <input
                        type="password"
                        name="password"
                        value={values.password}
                        onChange={handleChange}
                    />
                    <label>Password</label>
                    <span></span>
                    {errors.password && <p className="alert-danger">{errors.password}</p>}
                </div>
                <div className="pass">Forgot Password?</div>
                <input type="submit" value="Login" />

                <div className="auth"><span>OR</span></div>
                <div className="facebook">
                    <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                </div>
            </form>

            <div className="signup_link">
                Dont't have an account? <a href="#" onClick={() => setToggler(true)}>Sign up</a>
            </div>
        </div>



        {!successful ? (
            <div className="register" style={{
                display: toggler === false && 'none'
            }
            }>
                <div>
                    <h1>Register</h1>
                    <form onSubmit={handleRegister} noValidate >
                        <div className="txt_field">
                            <input
                                type="text"
                                name="full_name"
                                value={values.full_name}
                                onChange={handleChange}
                            />
                            <label>Full name</label>
                            <span></span>
                            {errors.full_name && <p className="alert">{errors.full_name}</p>}
                        </div>
                        <div className="txt_field">

                            <input
                                type="text"
                                name="email"
                                value={values.email}
                                onChange={handleChange}
                            />
                            <label>Email</label>
                            <span></span>
                            {errors.email && <p className="alert">{errors.email}</p>}
                        </div>
                        <div className="txt_field">

                            <input
                                type="password"
                                name="password"
                                value={values.password}
                                onChange={handleChange}
                            />
                            <label>Password</label>
                            <span></span>
                            {errors.password && <p className="alert">{errors.password}</p>}
                        </div>
                        <div className="txt_field">

                            <input
                                type="password"
                                name="password2"
                                value={values.password2}
                                onChange={handleChange}
                            />
                            <label>Password confirmation</label>
                            <span></span>
                            {errors.password2 && <p className="alert">{errors.password2}</p>}
                        </div>
                        <input type="submit" value="Register" />

                        <div className="auth"><span>OR</span></div>
                        <div className="facebook">
                            <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                        </div>
                    </form>

                    <div className="login_link">
                        Have an account? <a href="#" onClick={() => setToggler(false)}>Log in</a>
                    </div>
                </div>
                {message && (
                    <div className="form-group">
                        <div
                            className={
                                successful
                                    ? "alert alert-success"
                                    : "alert alert-danger"
                            }
                            role="alert"
                        >
                            {message}
                        </div>
                    </div>
                )}
            </div>
        ) : (
                <div className="register" >
                    <h1>inregistrare efectuata cu</h1>
                </div>
            )}




    </div>
)
}

export default Login
