import React,{useState}  from 'react'

function ProjectSection(props) {

    const [skillExperience, setSkillExperience] = useState(false);
    const [skillFields, setSkillFields] = useState(false);
    const [skill, setSkill] = useState({
        skill_name: ""
    });

    function deleteSkill(id) {
        props.setSkills(prevSkills => {
            return prevSkills.filter((skillItem, index) => {
                return index !== id;
            });
        });
    }

    function editSkill(id) {

        const skill1 = props.skills.filter((skillItem, index) => {
            return index === id;
        });

        setSkill({
            skill_name: skill1[0].skill_name
        })

        deleteSkill(id);

        setSkillFields(true);
    }

    function removeSkill(event) {

        setSkillFields(false);

        setSkill({
            skill_name: ""
        })

        event.preventDefault();
    }

    const formSchema = Yup.object().shape({
        skill_name: Yup.string()
            .required("This field is required!"),
    })

    function handleSubmit(values) {

        props.addSkill(values);

        setSkillFields(false);

        setSkill({
            skill_name: ""
        })

    }

    return (
        <div className="form-cls">
            <h3 className="text-secondary" onClick={() => setSkillExperience(!skillExperience)} ><i className="fa fa-file icon text-dark "></i> Projects</h3>
            <hr className="hr" />
            <div className="projects-section" style={{
                display: skillExperience === false && 'none'
            }}>
                <div className="form-row">
                    <div className="form-group col-md-12">
                        <label htmlFor="inputProject">Project*</label>
                        <input type="text" 
                        className="form-control" 
                        id="inputProject" 
                        placeholder="ex: Site pentru gestionarea unui magazin online" />
                    </div>
                </div>

                <div className="form-row">
                    <div className="form-group col-md-12">
                        <label htmlFor="inputDesc">Description</label>
                        <textarea 
                        className="form-control" 
                        id="inputDesc" 
                        placeholder="ex: Am folosit Spring boot si React" />
                    </div>
                </div>
                <hr className="hr" />
                <div className="toggler-additionalInfo">
                    <a href="#" className="toggler-additionalInfo"><i className="fa fa-plus-circle" aria-hidden="true"></i> Add another work experiencen</a>
                </div>
                <hr className="hr" />
            </div>
        </div>
    )
}

export default ProjectSection
