import React from 'react'
import { Formik } from 'formik';
import * as Yup from "yup";
import './Aplicare.css'

function Aplicare(props) {


    const validateSchema = Yup.object().shape({
        full_name: Yup.string()
            .required("This field is required!"),
        email: Yup.string()
            .email("Invalid email!")
            .required("This field is required!"),
        phone: Yup.string()
            .required("This field is required!"),
        cv: Yup.string()
            .required("This field is required!")
    })

    function handleSubmit(values) {

       props.showDetails(false);
       props.close(false);
    }

    return (
        <div className="form-aplicare">
            <Formik
                initialValues={
                    {
                        full_name: '',
                        email: '',
                        phone: '',
                        cv: '',
                    }
                }

                onSubmit={(values) => {
                    handleSubmit(values);

                }}

                validationSchema={validateSchema}>

                {props => (
                    <form onSubmit={props.handleSubmit}>


                        <div className="form-group">
                            <label htmlFor="inputfn">Full name*</label>
                            <input type="text"
                                name="full_name"
                                value={props.full_name}
                                className="form-control"
                                id="inputfn"
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}

                            />
                            {props.errors.full_name && props.touched.full_name && <p className="text-danger">{props.errors.full_name}</p>}
                        </div>

                        <div className="form-group">
                            <label htmlFor="inputEmail">Email*</label>
                            <input type="email"
                                name="email"
                                value={props.email}
                                className="form-control"
                                id="inputEmail"
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}
                            />
                            {props.errors.email && props.touched.email && <p className="text-danger">{props.errors.email}</p>}
                        </div>


                        <div className="form-group">
                            <label htmlFor="inputPhone">Telefon*</label>
                            <input type="text"
                                name="phone"
                                value={props.phone}
                                className="form-control"
                                id="inputPhone"
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}
                            />
                            {props.errors.phone && props.touched.phone && <p className="text-danger">{props.errors.phone}</p>}
                        </div>
                        <label htmlFor="validatedCustomFile">CV*</label>
                        <div className="form-group custom-file">
                     

                        <label class="custom-file-label" htmlFor="validatedCustomFile"></label>
                            <input type="file"
                                name="cv"
                                value={props.cv}
                                className="custom-file-input"
                                id="validatedCustomFile" 
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}
                            />
                   
                            {props.errors.cv && props.touched.cv && <p className="text-danger">{props.errors.cv}</p>}
                        </div>

                            <button type="submit" name="submit" className="btn-submit" >Aplica</button>
                   
                    </form>
                )}
            </Formik>

        </div>
    )
}

export default Aplicare
