import React, { useState } from 'react'
import { Formik } from 'formik';
import * as Yup from "yup";

function Aplicare() {

    const [openAditionalInfo, setOpenAditionalInfo] = useState(false);

    const validateSchema = Yup.object().shape({
        first_name: Yup.string()
            .required("This field is required!"),
        last_name: Yup.string()
            .required("This field is required!"),
        email: Yup.string()
            .email("Invalid email!")
            .required("This field is required!"),
        phone: Yup.string()
            .required("This field is required!")
    })

    function handleSubmit(values) {


    }

    return (
        <div >
            {/* <div className="form-group avatar col-md-2">
                        <img id="avatar" src="./images/cap.png" alt="3qwqq" />
            </div> */}

            <Formik
                initialValues={
                    {
                        first_name: '',
                        last_name: '',
                        email: '',
                        phone: '',
                        city: '',
                        nationality: '',
                        address: '',
                        dateOfBirth: '',
                        drivingLicence: '',
                        linkedin: '',
                        personalSite: ''
                    }
                }

                onSubmit={(values) => {
                    handleSubmit(values);

                }}

                validationSchema={validateSchema}>

                {props => (
                    <form onSubmit={props.handleSubmit} style={{textAlign :"center"}}>


                        <div className="form-group col-md-6">
                            <label htmlFor="inputfn">Full name*</label>
                            <input type="text"
                                name="first_name"
                                value={props.first_name}
                                className="form-control"
                                id="inputfn"
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}
                                placeholder="First name"

                            />
                            {props.errors.first_name && props.touched.first_name && <p className="text-danger">{props.errors.first_name}</p>}
                        </div>

                        <div className="form-group col-md-6">
                            <label htmlFor="inputCity1">Email*</label>
                            <input type="text"
                                name="city"
                                value={props.city}
                                className="form-control"
                                id="inputCity1"
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}
                            />
                            {props.errors.email && props.touched.email && <p className="text-danger">{props.errors.email}</p>}
                        </div>


                        <div className="form-group col-md-6">
                            <label htmlFor="inputEmail">Telefon*</label>
                            <input type="email"
                                name="email"
                                value={props.email}
                                className="form-control"
                                id="inputEmail"
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}
                                placeholder="Email"

                            />
                            {props.errors.email && props.touched.email && <p className="text-danger">{props.errors.email}</p>}
                        </div>
                        <div className="form-group col-md-6">
                            <label htmlFor="inputEmail">CV*</label>
                            <input type="email"
                                name="email"
                                value={props.email}
                                className="form-control"
                                id="inputEmail"
                                onChange={props.handleChange}
                                onBlur={props.handleBlur}
                                placeholder="Email"

                            />
                            {props.errors.email && props.touched.email && <p className="text-danger">{props.errors.email}</p>}
                        </div>


                        <div className="btn-next">
                            <button type="submit" name="submit" className="btn btn-primary" >Next  <i className="fa fa-arrow-right" aria-hidden="true"></i></button>
                        </div>
                    </form>
                )}
            </Formik>

        </div>
    )
}

export default Aplicare
