import React, { useState } from 'react'
import Aplicare from './Joburi/Aplicare';
import './Modal.css';

export const Modal = ({ show, close, data }) => {

    const [showApplay, setShowApplay] = useState(false);

    return (
        <div>
            <div className="back-drop" style={{
                transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
            }} onClick={close} />

                <div className="modal-wrapper" style={{
                    transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    top: data.p != null ? '15%' : '90%',
                    display: showApplay === true && 'none' 
                }} >
                    <div className="modal-header">
                        <div className="job-description">
                            <h2>{data.name}</h2>
                            {data.p != null ? <p>{data.p}</p> : null}
                        </div>
                        <span className="close-modal-btn" onClick={close} >x</span>
                    </div>
                    <div className="modal-content">
                        <div className="modal-body">
                            {data.p != null ? (
                                <div className="content">
                                    <h4>Job Description</h4>
                                    <p>{data.descriere}</p>
                                    <h4>Required Skill Sets</h4>
                                    <ul >
                                        {data.skills.map((item, nr) => {
                                            return (
                                                <li key={nr}><span>-{'>'} </span>{item}</li>
                                            );
                                        })}
                                    </ul>
                                    <h4>Personal Attributes</h4>
                                    <ul>
                                        {data.personalAttributes.map((item, nr) => {
                                            return (
                                                <li key={nr}><span>-{'>'} </span>{item}</li>
                                            );
                                        })}
                                    </ul>
                                    <h4>Further Details</h4>
                                    <ul>
                                        {data.moreDetails.map((item, nr) => {
                                            return (
                                                <li key={nr}><span>-{'>'} </span>{item}</li>
                                            );
                                        })}
                                    </ul>
                                </div>
                            ) : (
                                <div >
                                    {data.descriere != null ? (
                                        <div>
                                            <h4>Description</h4>
                                            <p>{data.descriere}</p>
                                        </div>
                                    ) : null}

                                    {data.category.map((item, nr) => {
                                        return (
                                            <ul key={nr}>
                                                <h4>{item.title}</h4>
                                                <li >{item.detalii}</li>
                                            </ul>
                                        );
                                    })}
                                </div>
                            )
                            }

                        </div>
                        {data.p != null ? (
                            <div className="modal-footer">
                                <button className="btn-cancel" onClick={close}>Close</button>
                                <button className="btn-apply" onClick={() => setShowApplay(true)}>Apply now</button>
                            </div>
                        ) : null}

                       {showApplay ===true ? <Aplicare showDetails={setShowApplay} close={close}/> : null }
                    </div>
                </div>
            
        </div>
    );
};
