import React, { useState } from 'react'
import './Strenghts.css'

function Strenghts({ strenghts }) {

    const [current, setCurrent] = useState(0);

    function setIndex(index){
        if(current==0)
        setCurrent(index);
    }

    if (!Array.isArray(strenghts) || strenghts.length <= 0) {
        return null;
    }
    return (
        <div className="bg-gray">
            <div className="strengths container">
                <div data-spy="scroll" data-target="#list-example" data-offset="0" class="scrollspy-example">
                    {strenghts.map((strenght, index) => {
                        return (
                            <div>
                            {index === current ?(
                            <ul>
                            {strenght.text.map((item,nr)=>{
                                        return (
                                            <li className={index === current ? 'list-item active' : 'list-item'} key={nr}>{item}</li>
                                        );
                                    })}
                            </ul>
                        ) : null }
                     </div>
                    )})}
                </div>
                <div id="list-example" class="list-group">
                    {strenghts.map((strenght, index) => {
                        return (
                            <p className={index === current ? 'list-group-item list-group-item-action active' : 'list-group-item list-group-item-action '} onClick={ () =>{setCurrent(index)}} >{strenght.title}</p>
                        );
                    })}
                </div>
            </div>
        </div>
    )
}

export default Strenghts;
