import React, { useState } from 'react';
import './Slider.css';
import { SliderData } from './SliderData';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';

function Slider({ slides }) {

	const [current, setCurrent] = useState(0);
	const length = slides.length;

	const nextSlide = () => {
		setCurrent(current === length - 1 ? 0 : current + 1);
	}

	const prevSlide = () => {
		setCurrent(current === 0 ? length - 1 : current - 1);
	}

	if (!Array.isArray(slides) || slides.length <= 0) {
		return null
	}

	// onClick={prevSlide}
	return (
		<section className="slider">
			<FiChevronLeft className="left-arrow" onClick={prevSlide} />
			<FiChevronRight className="right-arrow" onClick={nextSlide} />
			{SliderData.map((slide, index) => {
				return (
					<div>
						{/* <div className={index === current ? 'slide active' : 'slide'} key={index}>
							{index === current && (<img src={slide.image} alt="travel image" className="image" />)}
						</div> */}
						<div class="carousel slide" data-ride="carousel" id="carouselExampleIndicators">
							<ol class="carousel-indicators">
								<li class="active" data-slide-to="0" data-target="#carouselExampleIndicators"></li>
								<li data-slide-to="1" data-target="#carouselExampleIndicators"></li>
								<li data-slide-to="2" data-target="#carouselExampleIndicators"></li>
							</ol>
							<div class="carousel-inner">
								<div class="carousel-item active">
									<img alt="First slide" class="d-block w-100" src="https://i.postimg.cc/gcR0tR4Z/1.jpg" />
									<div class="carousel-caption d-none d-md-block">
										<h5>Slider One Item</h5>
										<p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Maxime, nulla, tempore. Deserunt excepturi quas vero.</p>
									</div>
								</div>
								<div class="carousel-item">
									<img alt="Second slide" class="d-block w-100" src="https://i.postimg.cc/pL0V5BVg/2.jpg" />
									<div class="carousel-caption d-none d-md-block">
										<h5>Slider One Item</h5>
										<p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Maxime, nulla, tempore. Deserunt excepturi quas vero.</p>
									</div>
								</div>
								<div class="carousel-item">
									<img alt="Third slide" class="d-block w-100" src="https://i.postimg.cc/NF7Bs6PC/3.jpg" />
									<div class="carousel-caption d-none d-md-block">
										<h5>Slider One Item</h5>
										<p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Maxime, nulla, tempore. Deserunt excepturi quas vero.</p>
									</div>
								</div>
							</div><a class="carousel-control-prev" data-slide="prev" href="#carouselExampleIndicators" role="button"><span aria-hidden="true" class="carousel-control-prev-icon"></span> <span class="sr-only">Previous</span></a> <a class="carousel-control-next" data-slide="next" href="#carouselExampleIndicators" role="button"><span aria-hidden="true" class="carousel-control-next-icon"></span> <span class="sr-only">Next</span></a>
						</div>


					</div>
				)


			})}
		</section>

	)
}

export default Slider;
