import React, { useState } from 'react';
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import { isEmail } from "validator";
import AuthService from "../services/auth.service";
import './Login.css';

function Login({ show, close }) {

    const [toggler, setToggler] = useState(false);
    const [full_name, setFull_name] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const required = value => {
        if (!value) {
            return (
                <div className="alert alert-danger" role="alert">
                    This field is required!
                </div>
            );
        }
    };

    const vFull_name = value => {
        if (value.length < 3 || value.length > 100) {
            return (
                <div className="alert alert-danger" role="alert">
                    The full_name must be between 3 and 100 characters.
                </div>
            );
        }
    }

    const vEmail = value => {
        if (!isEmail(value)) {
            return (
                <div className="alert alert-danger" role="alert">
                    This is not a valid email.
                </div>
            );
        }
    }

    const vPassword = value => {
        if (value.length < 6 || value.length > 50) {
            return (
                <div className="alert alert-danger" role="alert">
                    The password must be between 6 and 50 characters.
                </div>
            );
        }
    }

    function onChangeFull_name(event) {
        setFull_name(event.target.value);
    }

    function onChangeEmail(event) {
        setEmail(event.target.value);
    }

    function onChangePassword(event) {
        setPassword(event.target.value);
    }

    return (
        <div>
            <div className="back-drop" style={{
                transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
            }} onClick={close} />

            <div className="login" style={{
                display: toggler && 'none'
            }}>
                <h1>Login</h1>
                <Form className="form" method="post">
                    <div className="txt_field">
                        <label>Email</label>
                        <Input type="text"
                            className="input"
                            required />
                        <span></span>
                    </div>
                    <div className="txt_field">
                        <label>Password</label>
                        <Input type="password" className="input" required />
                        <span></span>
                    </div>
                    <div className="pass">Forgot Password?</div>
                    <Input type="submit" className="input" value="Login" />

                    <div className="auth"><span>OR</span></div>
                    <div className="facebook">
                        <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                    </div>
                </Form>

                <div className="signup_link">
                    Dont't have an account? <a href="#" onClick={() => setToggler(true)}>Sign up</a>
                </div>
            </div>

            <div className="register" style={{
                display: toggler === false && 'none'
            }
            }>
                <h1>Register</h1>
                <Form className="form" onSubmit={handleRegister} ref={c => {this.form=c; }} >
                    <div className="txt_field">
                        <label>Full name</label>
                        <Input
                            type="text"
                            className="input"
                            name="full_name"
                            value={full_name}
                            onChange={onChangeFull_name}
                            validations={[required, vFull_name]}
                        />
                        <span></span>
                    </div>
                    <div className="txt_field">
                        <label>Email</label>
                        <Input
                            type="text"
                            className="input"
                            name="email"
                            value={email}
                            onChange={onChangeEmail}
                            validations={[required, vEmail]}
                        />
                        <span></span>
                    </div>
                    <div className="txt_field">
                        <label>Password</label>
                        <Input
                            type="password"
                            className="input"
                            name="password"
                            value={password}
                            onChange={onChangePassword}
                            validations={[required, vPassword]}
                        />
                        <span></span>
                    </div>
                    <Input type="submit" className="input" value="Register" />

                    <div className="auth"><span>OR</span></div>
                    <div className="facebook">
                        <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                    </div>
                </Form>

                <div className="login_link">
                    Have an account? <a href="#" onClick={() => setToggler(false)}>Log in</a>
                </div>
            </div>


        </div>
    )
}

export default Login
