import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import logo from '../images/logo.png';
import Login from './Login';
import AuthService from "../services/auth.service";

import './Navbar.css';

function Navbar() {

  const currentUser = AuthService.getCurrentUser();

  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const [show, setShow] = useState(false);
  const closeModalHandler = () => setShow(false);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  function logOut() {
    AuthService.logout();
    window.location.reload();
  }


  window.addEventListener('resize', showButton);

  return (

    <nav className='navbar'>
      <div className='navbar-container'>
        <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          <img src={logo} alt="logo" className="logo" />
        </Link>
        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className='nav-item'>
            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
              Acasa
              </Link>
          </li>
          <li className='nav-item'>
            <Link
              to='/about'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Despre
              </Link>
          </li>
          <li className='nav-item'>
            <Link
              to='/jobs'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Joburi
              </Link>
          </li>
          {currentUser ? (
            <div>
              <li className='nav-item'>
                <Link
                  to='/user'
                  className='nav-links'
                  onClick={closeMobileMenu}
                >
                 User
              </Link>
              </li>

              <li className='nav-item'>
                  <button className={button ? ' btn btn-light nav-links-btn  ' : 'nav-links-mobile'} onClick={logOut}>Logout</button>
                </li>
            </div>
          ) : (
              <div>
                <li className='nav-item'>
                  <button className={button ? ' btn btn-light nav-links-btn  ' : 'nav-links-mobile'} onClick={() => { setShow(true); }}>Logheaza-te</button>
                </li>
              </div>
            )}
        </ul>
        {show && <Login show={show} close={closeModalHandler} />}
      </div>
    </nav>

  );
}

export default Navbar;
