import React, { useState } from 'react'
import { Formik } from 'formik';
import Row from './Row'
import * as Yup from "yup";

function EducationSection(props) {
    const [educationExperience, setEducationExperience] = useState(false);
    const [educationFields, setEducationFields] = useState(false);
    const [education, setEducation] = useState({
        degree: "",
        city: "",
        school: "",
        start: "",
        end: "",
        description: ""
    });

    function deleteEducation(id) {
        props.setEducations(prevEducations => {
            return prevEducations.filter((educationItem, index) => {
                return index !== id;
            });
        });
    }

    function editEducation(id) {

        const education1 = props.educations.filter((educationItem, index) => {
            return index === id;
        });

        setEducation({
            degree: education1[0].degree,
            city: education1[0].city,
            school: education1[0].school,
            start: education1[0].start,
            end: education1[0].end,
            descriere: education1[0].descriere
        })

        deleteEducation(id);

        setEducationFields(true);
    }

    function removeEducation(event) {

        setEducationFields(false);

        setEducation({
            degree: "",
            city: "",
            school: "",
            start: "",
            end: "",
            description: ""
        })

        event.preventDefault();
    }

    const formSchema = Yup.object().shape({
        degree: Yup.string()
            .required("This field is required!"),
        school: Yup.string()
            .required("This field is required!"),
        start: Yup.string()
            .required("This field is required!"),
        end: Yup.string()
            .required("This field is required!")
    })

    function handleSubmit(values) {

        props.addEducation(values);

        setEducationFields(false);

        setEducation({
            degree: "",
            city: "",
            school: "",
            start: "",
            end: "",
            description: ""
        })

    }

    return (
        <div className="form-cls">
            <h3 className="text-secondary" onClick={() => setEducation(!education)}><i className="fas fa-user-graduate icon text-dark"></i>Education and Qualifications</h3>
            <hr className="hr" />
            <div className="education-section" style={{
                display: education === false && 'none'
            }}>

                <div className="form-row">
                    {props.educations.length === 0 ? null : (
                        <div className="form-group col-md-12">
                            {props.educations.map((rowItem, index) => {
                                return (
                                    <Row
                                        key={index}
                                        id={index}
                                        title={rowItem.degree}
                                        start={rowItem.start}
                                        end={rowItem.end}
                                        onDelete={deleteEducation}
                                        onEdit={editEducation}
                                    />);
                            })}
                        </div>
                    )}
                </div>
                <div className="fields-education" style={{
                    display: educationFields === false && 'none'
                }}>
                    <Formik
                        enableReinitialize={true}

                        initialValues={
                            {
                                job_title: work.job_title,
                                city: work.city,
                                company: work.company,
                                start: work.start,
                                end: work.end,
                                descriere: work.descriere
                            }
                        }

                        onSubmit={(values, { resetForm }) => {
                            handleSubmit(values);
                            resetForm({ values: '' })
                        }}

                        validationSchema={formSchema}>

                        {props => (
                            <form onSubmit={props.handleSubmit} >
                                <div className="form-row">
                                    <div className="form-group col-md-6">
                                        <label for="inputDegree">Degree*</label>
                                        <input type="text" className="form-control" id="inputDegree" placeholder="Bachelor of Science" />
                                    </div>
                                    <div className="form-group col-md-6">
                                        <label for="inputCity">City*</label>
                                        <input type="text" className="form-control" id="inputCity" placeholder="" />
                                    </div>
                                </div>
                                <div className="form-row">
                                    <div className="form-group col-md-12">
                                        <label for="inputSchool">School*</label>
                                        <input type="tex" className="form-control" id="inputSchool" placeholder="UPIT University" />
                                    </div>
                                </div>
                                <div className="form-row">
                                    <div className="form-group col-md-6">
                                        <label for="inputStart">Start date*</label>
                                        <input type="date" className="form-control" id="inputStart" placeholder="" />
                                    </div>

                                    <div className="form-group col-md-6">
                                        <label for="inputEnd">End date*</label>
                                        <input type="date" className="form-control" id="inputEnd" placeholder="" />
                                    </div>
                                </div>

                                <div className="form-row">
                                    <div className="form-group col-md-12">
                                        <label for="inputDescriere">Description</label>
                                        <textarea className="form-control" id="inputDescriere" placeholder="" />
                                    </div>
                                </div>
                                <div className="select-option">
                                    <button onClick={removeWork} className="btn"><i className="fas fa-trash-alt"></i>Delete</button>
                                    <button type="submit" name="submit" className="btn"><i className="fas fa-save"></i>Save</button>
                                </div>
                            </form>
                        )}
                    </Formik>
                </div>

                <div className="toggler-additionalInfo">
                    <a href="#" className="toggler-additionalInfo"><i className="fa fa-plus-circle" aria-hidden="true"></i> Add another education experience</a>
                </div>
                <hr className="hr" />
            </div>
        </div >
    )
}

export default EducationSection
