import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { isEmail } from "validator";
import AuthService from "../services/auth.service";
import './Login.css';

function Login({ show, close }) {

    let history = useHistory();

    const [toggler, setToggler] = useState(false);
    const [values, setValues] = useState({
        full_name: '',
        email: '',
        password: '',
        password2: ''
    });
    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);

    const [message, setMessage] = useState("");
    const [successful, setSuccessful] = useState(true);


    function validate(values) {
        let errors = {};

        if (!values.full_name.trim()) {
            errors.full_name = "Full name required";
        }

        if (!values.email) {
            errors.email = "Email required";
        } else if (!isEmail(values.email)) {
            errors.email = "Email address is invalid";
        }

        if (!value.password) {
            errors.password = 'Password is required';
        } else if (values.password.length < 6 && values.password.length > 50) {
            errors.password = "Password must be between 6 and 50 characters ";
        }

        if (!values.password2) {
            errors.password2 = "Password confirmation is required";
        } else if (values.password2 !== values.password) {
            errors.password2 = "Password do no match";
        }
    }

    function handleLogin(e) {
        e.preventDefault();

        AuthService.login(email, password).then(
            () => {
                history.push("/user");
                window.location.reload();
            },
            error => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();

                setMessage(resMessage);
            }
        )
    }

    function handleRegister(e) {
        e.preventDefault();

        AuthService.register(
            full_name,
            email,
            password
        ).then(
            response => {
                setMessage(response.data.message);
                setSuccessful(true);
            },
            error => {
                const resMessage = (
                    error.response &&
                    error.response.data &&
                    error.response.data.message) || error.message || error.toString();
                setMessage(resMessage);
                setSuccessful(false);
            }
        );

    }

    return (
        <div>
            <div className="back-drop" style={{
                transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
            }} onClick={close} />

            <div className="login" style={{
                display: toggler && 'none'
            }}>
                <h1>Login</h1>
                <form onSubmit={handleLogin} noValidate>
                    <div className="txt_field">
                        <input
                            type="text"
                            name="email"
                            value={email}
                            onChange={handleChange}
                            validations={[required]}
                        />
                        <label>Email</label>
                        <span></span>
                    </div>
                    <div className="txt_field">
                        <input
                            type="password"
                            name="password"
                            value={password}
                            onChange={handleChange}
                            validations={[required]}
                        />
                        <label>Password</label>
                        <span></span>
                    </div>
                    <div className="pass">Forgot Password?</div>
                    <input type="submit" value="Login" />

                    <div className="auth"><span>OR</span></div>
                    <div className="facebook">
                        <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                    </div>
                </form>

                <div className="signup_link">
                    Dont't have an account? <a href="#" onClick={() => setToggler(true)}>Sign up</a>
                </div>

                {message && (
                    <div className="form-group container">
                        <div className="alert alert-danger" role="alert">
                            {message}
                        </div>
                    </div>
                )}
            </div>

            <div className="register" style={{
                display: toggler === false && 'none'
            }
            }>
                {successful && (
                    <div>
                        <h1>Register</h1>
                        <form onSubmit={handleRegister} >
                            <div className="txt_field">
                                <input
                                    type="text"
                                    name="full_name"
                                    value={full_name}
                                    onChange={handleChange}
                                    validations={[required, vFull_name]}
                                />
                                <label>Full name</label>
                                <span></span>
                            </div>
                            <div className="txt_field">

                                <input
                                    type="text"
                                    name="email"
                                    value={email}
                                    onChange={handleChange}
                                    validations={[required, vEmail]}
                                />
                                <label>Email</label>
                                <span></span>
                            </div>
                            <div className="txt_field">

                                <input
                                    type="password"
                                    name="password"
                                    value={password}
                                    onChange={handleChange}
                                    validations={[required, vPassword]}
                                />
                                <label>Password</label>
                                <span></span>
                            </div>
                            <input type="submit" value="Register" />

                            <div className="auth"><span>OR</span></div>
                            <div className="facebook">
                                <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                            </div>
                        </form>

                        <div className="login_link">
                            Have an account? <a href="#" onClick={() => setToggler(false)}>Log in</a>
                        </div>
                    </div>
                )}

                {message && (
                    <div className="form-group">
                        <div
                            className={
                                successful
                                    ? "alert alert-success"
                                    : "alert alert-danger"
                            }
                            role="alert"
                        >
                            {message}
                        </div>
                    </div>
                )}
            </div>


        </div>
    )
}

export default Login
