import React,{useState}  from 'react'
import { Formik } from 'formik';
import Row from './Row'
import * as Yup from "yup";

function HobbySection(props) {

    const [hobbyExperience, setHobbyExperience] = useState(false);
    const [hobbyFields, setHobbyFields] = useState(false);
    const [hobby, setHobby] = useState({
        hobby_name: ""
    });

    function deleteHobby(id) {
        props.setHobbys(prevHobbys => {
            return prevHobbys.filter((hobbyItem, index) => {
                return index !== id;
            });
        });
    }

    function editHobby(id) {

        const hobby1 = props.hobbys.filter((hobbyItem, index) => {
            return index === id;
        });

        setHobby({
            hobby_name: hobby1[0].hobby_name
        })

        deleteHobby(id);

        setHobbyFields(true);
    }

    function removeHobby(event) {

        setHobbyFields(false);

        setHobby({
            hobby_name: ""
        })

        event.preventDefault();
    }

    const formSchema = Yup.object().shape({
        hobby_name: Yup.string()
            .required("This field is required!"),
    })

    function handleSubmit(values) {

        props.addHobby(values);

        setHobbyFields(false);

        setHobby({
            hobby_name: ""
        })

    }


    return (
        <div className="form-cls">
            <h3 className="text-secondary" onClick={() => setHobby(!hobby)}><i class="fas fa-paint-brush icon text-dark"></i>Hobbys</h3>
            <hr className="hr" />
            <div className="hobbys-section" style={{
                display: hobby === false && 'none'
            }}>
                <div className="form-row">
                    <div className="form-group col-md-12">
                        <label for="inputHobby">Hobby name*</label>
                        <input type="text" className="form-control" id="inputHobby" placeholder="ex:Football" />
                    </div>
                </div>

                <hr className="hr" />
                <div className="toggler-additionalInfo">
                    <a href="#" className="toggler-additionalInfo"><i className="fa fa-plus-circle" aria-hidden="true"></i> Add another work experiencen</a>
                </div>
                <hr className="hr" />
            </div>
        </div>
    )
}

export default HobbySection
