import React, { useState } from 'react'
import { Formik } from 'formik';
import Row from './Row'
import * as Yup from "yup";

function WorkSection(props) {

    const [workExperience, setWorkExperience] = useState(false);
    const [workFields, setWorkFields] = useState(false);
    const [work, setWork] = useState({
        job_title: "",
        city: "",
        company: "",
        start: "",
        end: "",
        descriere: ""
    });

    function deleteWork(id) {
        props.setWorks(prevWorks => {
            return prevWorks.filter((workItem, index) => {
                return index !== id;
            });
        });
    }

    function editWork(id) {
        //cautam in rows cel care are id ul respectiv.
        console.log(props.works);
    }

    function removeWork(event) {

        setWorkFields(false);

        setWork({
            job_title: "",
            city: "",
            company: "",
            start: "",
            end: "",
            descriere: ""
        })

        event.preventDefault();
    }

    function submitWork(values) {

        props.addWork(values);

        setWorkFields(false);


    }


    const formSchema = Yup.object().shape({
        job_title: Yup.string()
            .required("This field is required!"),
        company: Yup.string()
            .required("This field is required!"),
        start: Yup.string()
            .required("This field is required!"),
        end: Yup.string()
            .required("This field is required!")
    })

    function handleSubmit(values) {

        submitWork(values);
        setWork({
            job_title: "",
            city: "",
            company: "",
            start: "",
            end: "",
            descriere: ""
        })

    }
    const formik = useFormik({
        initialValues: {
            job_title: '',
        },
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2));
        },
    });

    return (
        <div>
            <div className="form-cls">


                {/************************WORK**************************/}

                <h3 className="text-secondary" onClick={() => setWorkExperience(!workExperience)}><i className="fa fa-briefcase icon text-dark"></i>Work experience</h3>
                <hr className="hr" />
                <div className="work-section" style={{
                    display: workExperience === false && 'none'
                }}>

                    <div className="form-row">
                        {props.works.length === 0 ? null : (
                            <div className="form-group col-md-12">
                                {props.works.map((rowItem, index) => {
                                    return (
                                        <Row
                                            key={index}
                                            id={index}
                                            title={rowItem.job_title}
                                            start={rowItem.start}
                                            end={rowItem.end}
                                            onDelete={deleteWork}
                                            onEdit={editWork}
                                        />);
                                })}
                            </div>
                        )}
                    </div>

                    <div className="fields-work" style={{
                        display: workFields === false && 'none'
                    }}>

                        <form onSubmit={formik.handleSubmit} >

                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label htmlFor="nputJobTitle">Job title*</label>
                                    <input type="text"
                                        name="job_title"
                                        onChange={props.handleChange}
                                        onBlur={props.handleBlur}
                                        value={props.job_title}
                                        className="form-control"
                                        id="inputJobTitle"
                                        placeholder="" />
                                    {props.errors.job_title && props.touched.job_title && <p className="text-danger">{props.errors.job_title}</p>}
                                </div>
                               </div>
                            <div className="select-option">
                                <button onClick={removeWork} className="btn"><i className="fas fa-trash-alt"></i>Delete</button>
                                <button type="submit" name="submit" className="btn"><i className="fas fa-save"></i>Save</button>
                            </div>
                        </form>

                    </div>

                    <div className="toggler-additionalInfo">
                        <a href="#/" className="btn-additionalInfo" onClick={() => { setWorkFields(true) }}><i className="fa fa-plus-circle" aria-hidden="true"></i> Add another work experience</a>
                    </div>
                    <hr className="hr" />
                </div>

            </div>
        </div>
    )
}

export default WorkSection
