import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { isEmail } from "validator";
import AuthService from "../services/auth.service";
import './Login.css';

function Login({ show, close }) {

    let history = useHistory();

    const [toggler, setToggler] = useState(false);

    const [full_name, setFull_name] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");


    const [errorfull_name, seterrorFull_name] = useState("");
    const [erroremail, seterrorEmail] = useState("");
    const [errorpassword, seterrorPassword] = useState("");
    const [errorpassword2, seterrorPassword2] = useState("");


    const [isSubmitting, setIsSubmitting] = useState(false);
    const [isSubmitted, setIsSubmitted] = useState(false);

    function submitForm() {
        setIsSubmitted(true);
    }

    function validate() {


        if (!full_name.trim()) {
           seterrorFull_name("Full name required");
        }

        if(!email){
            seterrorEmail("Email required");
        }
        else if(!isEmail(email)){
            seterrorEmail("Email address is invalid");
        }

        if (!password) {
            seterrorPassword('Password is required');
        } else if (password.length < 6 && password.length > 50) {
            seterrorPassword("Password must be between 6 and 50 characters ");
        }

        if (!password2) {
            seterrorPassword2("Password confirmation is required");
        } else if (password2 !== password) {
            seterrorPassword2("Password do no match");
        }
 
    }

    function onChangeFull_name(event) {
        setFull_name(event.target.value);
    }

    function onChangeEmail(event) {
        setEmail(event.target.value);
    }

    function onChangePassword(event) {
        setPassword(event.target.value);
    }

    function onChangePassword2(event){
        setPassword2(event.target.value);
    }

    // function handleLogin(e) {
    //     e.preventDefault();

    //     AuthService.login(values.email, values.password).then(
    //         () => {
    //             history.push("/user");
    //             window.location.reload();

    //             setSuccessful(true);
    //         },
    //         error => {
    //             setErrors(validate(values));
    //             setIsSubmitting(true);
    //         }
    //     )
    // }

    function handleRegister(e) {
        e.preventDefault();

        validate();
        setIsSubmitting(true);


    }

    useEffect(
        () => {
            if (Object.keys(errors).length === 0 && isSubmitting) {
                submitForm();
            }
        },
        [errors]
    );

    return (
        <div>
            <div className="back-drop" style={{
                transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
            }} onClick={close} />

            <div className="login" style={{
                display: toggler && 'none'
            }}>
                <h1>Login</h1>
                <form onSubmit={handleLogin} noValidate>
                    <div className="txt_field">
                        <input
                            type="text"
                            name="email"
                            value={email}
                            onChange={handleChange}
                        />
                        <label>Email</label>
                        <span></span>
               
                    </div>
                    <div className="txt_field">
                        <input
                            type="password"
                            name="password"
                            value={password}
                            onChange={handleChange}
                        />
                        <label>Password</label>
                        <span></span>
                        {errors.password && <p>{errors.password}</p>}
                    </div>
                    <div className="pass">Forgot Password?</div>
                    <input type="submit" value="Login" />

                    <div className="auth"><span>OR</span></div>
                    <div className="facebook">
                        <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                    </div>
                </form>

                <div className="signup_link">
                    Dont't have an account? <a href="#" onClick={() => setToggler(true)}>Sign up</a>
                </div>

            </div>

            <div className="register" style={{
                display: toggler === false && 'none'
            }
            }>

                {!isSubmitted ? (
                    <div>
                        <h1>Register</h1>
                        <form onSubmit={handleRegister} >
                            <div className="txt_field">
                                <input
                                    type="text"
                                    name="full_name"
                                    value={full_name}
                                    onChange={onChangeFull_name}
                                />
                                <label>Full name</label>
                                <span></span>
                                {errorfull_name && <p>{errorfull_name}</p>}
                            </div>
                            <div className="txt_field">

                                <input
                                    type="text"
                                    name="email"
                                    value={email}
                                    onChange={onChangeEmail}
                                />
                                <label>Email</label>
                                <span></span>
                                {erroremail && <p>{erroremail}</p>}
                            </div>
                            <div className="txt_field">

                                <input
                                    type="password"
                                    name="password"
                                    value={password}
                                    onChange={onChangePassword}
                                />
                                <label>Password</label>
                                <span></span>
                                {errorpassword && <p>{errorpassword}</p>}
                            </div>
                            <div className="txt_field">

                                <input
                                    type="password"
                                    name="password2"
                                    value={password2}
                                    onChange={onChangePassword2}
                                />
                                <label>Password confirmation</label>
                                <span></span>
                                {errorpassword2 && <p>{errorpassword2}</p>}
                            </div>
                            <input type="submit" value="Register" />

                            <div className="auth"><span>OR</span></div>
                            <div className="facebook">
                                <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                            </div>
                        </form>

                        <div className="login_link">
                            Have an account? <a href="#" onClick={() => setToggler(false)}>Log in</a>
                        </div>
                    </div>
                ) : (
                        <div>
                            <h1>inregistrare efectuata cu succes!</h1>
                        </div>
                    )}

            </div>


        </div>
    )
}

export default Login
