import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useHistory } from 'react-router-dom';
import logo from '../images/logo.png';
import Login from './Login';
import AuthService from "../services/auth.service";
import Sidebar from './Sidebar';

import './Navbar.css';

function Navbar() {

  let history = useHistory();

  const currentUser = AuthService.getCurrentUser();

  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const [show, setShow] = useState(false);
  const closeModalHandler = () => setShow(false);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  function logOut() {
    AuthService.logout();
    history.push("/home");
    window.location.reload();
  }


  window.addEventListener('resize', showButton);

  return (

    <nav >
    <div className='navbar'>
      <div className='navbar-container'>
        <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          <img src={logo} alt="logo" className="logo" />
        </Link>
        <div className='menu-icon-sidebar' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-tachometer-alt'} />
        </div>
        <div className='menu-icon-navbar' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className='nav-item'>
            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
              Acasa
              </Link>
          </li>
          <li className='nav-item'>
            <Link
              to='/about'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Despre
              </Link>
          </li>
          <li className='nav-item'>
            <Link
              to='/jobs'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Joburi
              </Link>
          </li>
          <li className='nav-item'>
            <Link
              to='/makeCV'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              MakeCV
              </Link>
          </li>
          {currentUser ? (
            <li className='nav-item'>
              <Link
                to={currentUser.role === "ROLE_ADMIN" ? "/admin" : "/user"}
                className='nav-links'
                onClick={closeMobileMenu}
              >
                {currentUser.role === "ROLE_ADMIN" ? "Admin" : "User"}
              </Link>
            </li>
          ) : null}
          <div>
            <li className='nav-item'>
              <button className={button ? ' btn btn-light nav-links-btn  ' : 'nav-links-mobile'} onClick={() => { currentUser ? logOut() : setShow(true) }}>{currentUser ? "Log-out" : "Logheaza-te"}</button>
            </li>
          </div>

        </ul>
        {show && <Login show={show} close={closeModalHandler} />}
      </div>
      </div>
      <div>
      <Sidebar />   
      </div>

    </nav>

  );
}

export default Navbar;
