import React, { useState } from 'react'
import Note from './Note'
import './MakeCV.css'

function MakeCV() {

    const [openAditionalInfo, setOpenAditionalInfo] = useState(false);
    const [stateForm, setStateForm] = useState(1);
    const [workExperience, setWorkExperience] = useState(false);
    const [education, setEducation] = useState(false);
    const [skill, setSkill] = useState(false);
    const [language, setLanguage] = useState(false);
    const [hobby, setHobby] = useState(false);
    const [project, setProject] = useState(false);

    const [notes, setNotes] = useState([]);

    function addNote(newNote) {
        setNotes(prevNotes => {
            return [...prevNotes, newNote];
        });
    }

    function deleteNote(id) {
        setNotes(prevNotes => {
            return prevNotes.filter((noteItem, index) => {
                return index !== id;
            });
        });
    }

    const [work, setWork] = useState({
        job_title: "",
        city: "",
        company: "",
        start: "",
        end: "",
        descriere: ""
    })

    function handleChange(event) {
        const { name, value } = event.target;
        setWork((prevNote) => {
            return {
                ...prevNote,
                [name]: value
            }
        })
    }

    function removeWork(event) {

        addNote(work);

        setWork({
            job_title: "",
            city: "",
            company: "",
            start: "",
            end: "",
            descriere: ""
        })

        event.preventDefault();
    }

    function submitWork(event) {

        addNote(work);

        setWork({
            job_title: "",
            city: "",
            company: "",
            start: "",
            end: "",
            descriere: ""
        })

        event.preventDefault();
    }

    return (
        <div style={{ background: '#f1f1f1', height: '100%', padding: '50px' }}>
            <div className="container proggres-bar">
                <ul className="progressbar">
                    <li className="active" onClick={() => setStateForm(1)}>Personal</li>
                    <li className={(stateForm === 2 || stateForm === 3) && "active"} onClick={() => setStateForm(2)}>Experiences</li>
                    <li className={stateForm === 3 && "active"} onClick={() => setStateForm(3)}>Finish</li>
                </ul>

                {/**********************************PersonalDetails form********************************************************/}

                <div className="personalDetails" style={{
                    display: stateForm !== 1 && 'none'
                }}>
                    <h3>Personal details</h3>
                    <hr className="hr" />
                    <form className="form-cls">
                        {/* <div className="form-group avatar col-md-2">
                            <img id="avatar" src="./images/cap.png" alt="" />
                        </div> */}

                        <div className="form-row">
                            <div className="form-group col-md-6">
                                <label for="inputfn">First name*</label>
                                <input type="text" className="form-control" id="inputfn" placeholder="First name" />
                            </div>
                            <div className="form-group col-md-6">
                                <label for="inputln">Last name*</label>
                                <input type="text" className="form-control" id="inputln" placeholder="Last name" />
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-6">
                                <label for="inputEmail">Email address*</label>
                                <input type="email" className="form-control" id="inputEmail" placeholder="Email" />
                            </div>
                            <div className="form-group col-md-6">
                                <label for="inputPhone">Phone number*</label>
                                <input type="text" className="form-control" id="inputPhone" placeholder="Phone" />
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group col-md-12">
                                <label for="inputAddress">Address</label>
                                <input type="text" className="form-control" id="inputAddress" placeholder="1234 Main St" />
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group col-md-6">
                                <label for="inputCity1">City</label>
                                <input type="text" className="form-control" id="inputCity1" />
                            </div>

                            <div className="form-group col-md-6">
                                <label for="inputZip">Zip code</label>
                                <input type="text" className="form-control" id="inputZip" />
                            </div>
                        </div>

                        <div className="additionalInfo" style={{
                            display: openAditionalInfo === false && 'none'
                        }}>

                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label for="inputDateBirth">Date of birth</label>
                                    <input type="date" data-date-format="dd-mm-yyyy" className="form-control" id="inputDateBirth" />
                                </div>

                                <div className="form-group col-md-6">
                                    <label for="inputPlaceBirth">Place of birth</label>
                                    <input type="text" className="form-control" id="inputPlaceBirth" />
                                </div>
                            </div>
                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label for="inputDrivingLicence">Driving licence</label>
                                    <input type="text" className="form-control" id="inputDrivingLicence" placeholder="B" />
                                </div>

                                <div className="form-group col-md-6">
                                    <label for="inputGender">Gender</label>
                                    <select id="inputGender" className="form-control">
                                        <option value>Choose</option>
                                        <option>Male</option>
                                        <option>Female</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label for="inputNationality">Nationality</label>
                                    <input type="text" className="form-control" id="inputNationality" />
                                </div>

                                <div className="form-group col-md-6">
                                    <label for="inputMaritalStatus">Marital status</label>
                                    <input type="text" className="form-control" id="inputMaritalStatus" />
                                </div>
                            </div>
                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label for="inputLinkedIn">LinkedIn</label>
                                    <input type="text" className="form-control" id="inputLinkedIn" />
                                </div>

                                <div className="form-group col-md-6">
                                    <label for="inputWebsite">Website</label>
                                    <input type="text" className="form-control" id="inputWebsite" />
                                </div>
                            </div>
                        </div>

                        <div className="toggler-additionalInfo">
                            <a href="#" onClick={() => setOpenAditionalInfo(!openAditionalInfo)}><i className={openAditionalInfo === false ? "fa fa-plus-circle" : "fa fa-minus-circle"} aria-hidden="true"></i> Additional information</a>
                        </div>

                        <hr className="hr" />

                        <div className="btn-next">
                            <button type="submit" className="btn btn-primary" onClick={() => setStateForm(2)}>Next  <i className="fa fa-arrow-right" aria-hidden="true"></i></button>
                        </div>

                    </form>
                </div>


                {/**********************************Experience form********************************************************/}

                <div className="experience" style={{
                    display: stateForm !== 2 && 'none'
                }}>
                    <h3>Experiences</h3>
                    <hr className="hr" />
                    <form className="form-cls">
                        {/* <div className="form-group avatar col-md-2">
                            <img id="avatar" src="./images/cap.png" alt="" />
                        </div> */}

                        {/************************WORK**************************/}

                        <h3 className="text-secondary" onClick={() => setWorkExperience(!workExperience)}><i className="fa fa-briefcase icon text-dark"></i>Work experience</h3>
                        <hr className="hr" />
                        <div className="work-section" style={{
                            display: workExperience === false && 'none'
                        }}>

                            <div className="form-row">
                                {notes.length === 0 ? null : (
                                    <div className="form-group col-md-12">
                                        {notes.map((noteItem, index) => {
                                            return (
                                                <Note
                                                    key={index}
                                                    id={index}
                                                    title={noteItem.job_title}
                                                    start={noteItem.start}
                                                    end={noteItem.end}
                                                    onDelete={deleteNote}
                                                />);
                                        })}
                                    </div>
                                )}
                             
                                <div className="form-group col-md-6">
                                    <label for="nputJobTitle">Job title*</label>
                                    <input type="text"
                                        name="job_title"
                                        onChange={handleChange}
                                        value={work.job_title}
                                        className="form-control"
                                        id="inputJobTitle"
                                        placeholder="" />
                                </div>
                                <div className="form-group col-md-6">
                                    <label for="inputCity2">City*</label>
                                    <input type="text"
                                        name="city"
                                        onChange={handleChange}
                                        value={work.city}
                                        className="form-control"
                                        id="inputCity2" placeholder="" />
                                </div>
                            </div>
                            <div className="form-row">
                                <div className="form-group col-md-12">
                                    <label for="inputCompany">Company*</label>
                                    <input type="text"
                                        name="company"
                                        onChange={handleChange}
                                        value={work.company}
                                        className="form-control"
                                        id="inputCompany" placeholder="" />
                                </div>
                            </div>
                            <div className="form-row">
                                <div className="form-group col-md-5">
                                    <label for="inputStartDate">Start date*</label>
                                    <input type="date"
                                        name="start"
                                        onChange={handleChange}
                                        value={work.start}
                                        className="form-control"
                                        id="inputStartDate" placeholder="" />
                                </div>

                                <div className="form-group col-md-5">
                                    <label for="inputEndDate">End date*</label>
                                    <input type="date"
                                        name="end"
                                        onChange={handleChange}
                                        value={work.end}
                                        className="form-control"
                                        id="inputEndDate" placeholder="" />
                                </div>
                                <div className="form-group col-md-2">
                                    <label for="inputEmail">In curs</label>
                                    <br />

                                </div>
                            </div>

                            <div className="form-row">
                                <div className="form-group col-md-12">
                                    <label for="inputDescription">Description</label>
                                    <textarea name="descriere" onChange={handleChange}
                                        value={work.descriere}
                                        className="form-control"
                                        id="inputDescription" placeholder="" />
                                </div>
                            </div>
                            <div className="select-option">
                                <button onClick={removeWork} className="btn"> <i class="fas fa-trash-alt"></i>Delete</button>
                                <button onClick={submitWork} className="btn"><i class="fas fa-save"></i>Save</button>
                            </div>

                            <hr className="hr" />
                            <div className="toggler-additionalInfo">
                                <a href="#" ><i className="fa fa-plus-circle" aria-hidden="true"></i> Add another work experience</a>
                            </div>
                            <hr className="hr" />
                        </div>




                        <div className="two-btn">
                            <button className="btn btn-primary btn-prev" onClick={() => setStateForm(1)}><i class="fa fa-arrow-left" aria-hidden="true"> Previous</i></button>

                            <button type="submit" className="btn btn-primary" onClick={() => setStateForm(3)}>Next  <i class="fa fa-arrow-right" aria-hidden="true"></i></button>
                        </div>

                    </form>
                </div>

                {/**********************************Finish form********************************************************/}

                <div className="finish" style={{
                    display: stateForm !== 3 && 'none'
                }}>

                    <div className="two-btn">
                        <button className="btn btn-primary btn-prev" onClick={() => setStateForm(2)}><i class="fa fa-arrow-left" aria-hidden="true"> Previous</i></button>

                        <button type="submit" className="btn btn-primary">Export pdf</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MakeCV
