import React, { useState } from 'react'
import Note from './Note'
import { Formik } from 'formik';
import * as Yup from "yup";
import PDF from './PDF'
import './MakeCV.css'

function MakeCV() {

    const [openAditionalInfo, setOpenAditionalInfo] = useState(false);
    const [stateForm, setStateForm] = useState(1);
    const [workExperience, setWorkExperience] = useState(false);
    // const [education, setEducation] = useState(false);
    // const [skill, setSkill] = useState(false);
    // const [language, setLanguage] = useState(false);
    // const [hobby, setHobby] = useState(false);
    // const [project, setProject] = useState(false);

    const [workFields, setWorkFields] = useState(false);

    const [notes, setNotes] = useState([]);

    function addNote(newNote) {
        setNotes(prevNotes => {
            return [...prevNotes, newNote];
        });
    }

    function deleteNote(id) {
        setNotes(prevNotes => {
            return prevNotes.filter((noteItem, index) => {
                return index !== id;
            });
        });
    }


    const [work, setWork] = useState({
        job_title: "",
        city: "",
        company: "",
        start: "",
        end: "",
        descriere: ""
    })

    function handleChange(event) {
        const { name, value } = event.target;
        setWork((prevNote) => {
            return {
                ...prevNote,
                [name]: value
            }
        })
    }

    function removeWork(event) {

        setWorkFields(false);

        setWork({
            job_title: "",
            city: "",
            company: "",
            start: "",
            end: "",
            descriere: ""
        })

        event.preventDefault();
    }

    function submitWork(event) {

        addNote(work);

        setWork({
            job_title: "",
            city: "",
            company: "",
            start: "",
            end: "",
            descriere: ""
        })

        setWorkFields(false);

        event.preventDefault();
    }

    const formSchema = Yup.object().shape({
        first_name: Yup.string()
            .required("This field is required!"),
        last_name: Yup.string()
            .required("This field is required!"),
        email: Yup.string()
            .email("Invalid email!")
            .required("This field is required!"),
        phone: Yup.string()
            .required("This field is required!")
    })

    function handleSubmit(values) {

        const { first_name, email } = values;
        console.log(first_name+" "+email)


    }

    return (
        <div style={{ background: '#f1f1f1', height: '100%', padding: '50px' }}>

            <Formik
                initialValues={
                    {
                        first_name: '',
                        last_name: '',
                        email: '',
                        phone: '',
                        city: '',
                        nationality: '',
                        address: '',
                        dateOfBirth: '',
                        drivingLicence: '',
                        linkedin: '',
                        personalSite: ''
                    }
                }

                onSubmit={(values, { resetForm }) => {
                    handleSubmit(values);
                    resetForm({ values: '' })
                }}

                validationSchema={formSchema}>

                {props => (
                    <form>
                        <div className="container proggres-bar">
                            <ul className="progressbar">
                                <li className="active" onClick={() => setStateForm(1)}>Personal</li>
                                <li className={(stateForm === 2 || stateForm === 3) ? "active" : null} onClick={() => setStateForm(2)}>Experiences</li>
                                <li className={stateForm === 3 ? "active" : null} onClick={() => setStateForm(3)}>Finish</li>
                            </ul>

                            {/**********************************PersonalDetails form********************************************************/}

                            <div className="personalDetails" style={{
                                display: stateForm !== 1 && 'none'
                            }}>
                                <h3>Personal details</h3>
                                <hr className="hr" />
                                <div className="form-cls">
                                    {/* <div className="form-group avatar col-md-2">
                            <img id="avatar" src="./images/cap.png" alt="3qwqq" />
                        </div> */}

                                    <div className="form-row">
                                        <div className="form-group col-md-6">
                                            <label htmlFor="inputfn">First name*</label>
                                            <input type="text"
                                                name="first_name"
                                                value={props.first_name}
                                                className="form-control"
                                                id="inputfn"
                                                onChange={props.handleChange}
                                                onBlur={props.handleBlur}
                                                placeholder="First name" />
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label htmlFor="inputln">Last name*</label>
                                            <input type="text"
                                                name="last_name"
                                                value={props.last_name}
                                                className="form-control"
                                                id="inputln"
                                                onChange={props.handleChange}
                                                onBlur={props.handleBlur}
                                                placeholder="Last name" />
                                        </div>
                                    </div>
                                    <div className="form-row">
                                        <div className="form-group col-md-6">
                                            <label htmlFor="inputEmail">Email address*</label>
                                            <input type="email"
                                                name="email"
                                                value={props.email}
                                                className="form-control"
                                                id="inputEmail"
                                                onChange={props.handleChange}
                                                onBlur={props.handleBlur}
                                                placeholder="Email" />
                                        </div>
                                        {props.errors.email && props.touched.email && <p className="text-danger">{props.errors.email}</p>}
                                        <div className="form-group col-md-6">
                                            <label htmlFor="inputPhone">Phone number*</label>
                                            <input type="text"
                                                name="phone"
                                                value={props.phone}
                                                className="form-control"
                                                id="inputPhone"
                                                onChange={props.handleChange}
                                                onBlur={props.handleBlur}
                                                placeholder="Phone" />
                                        </div>
                                    </div>

                                    <div className="form-row">
                                        <div className="form-group col-md-6">
                                            <label htmlFor="inputCity1">City</label>
                                            <input type="text"
                                                name="city"
                                                value={props.city}
                                                className="form-control"
                                                id="inputCity1"
                                                onChange={props.handleChange}
                                                onBlur={props.handleBlur}
                                            />
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label htmlFor="inputNationality">Nationality</label>
                                            <input type="text"
                                                name="nationality"
                                                value={props.nationality}
                                                className="form-control"
                                                id="inputNationality"
                                                onChange={props.handleChange}
                                                onBlur={props.handleBlur} />
                                        </div>
                                    </div>

                                    <div className="form-row">
                                        <div className="form-group col-md-12">
                                            <label htmlFor="inputAddress">Address</label>
                                            <input type="text"
                                                name="address"
                                                value={props.address}
                                                className="form-control"
                                                id="inputAddress"
                                                placeholder="1234 Main St"
                                                onChange={props.handleChange}
                                                onBlur={props.handleBlur} />
                                        </div>
                                    </div>




                                    <div className="additionalInfo" style={{
                                        display: openAditionalInfo === false && 'none'
                                    }}>


                                        <div className="form-row">
                                            <div className="form-group col-md-6">
                                                <label htmlFor="inputDateBirth">Date of birth</label>
                                                <input type="date"
                                                    name="dateOfBirth"
                                                    value={props.dateOfBirth}
                                                    data-date-format="dd-mm-yyyy"
                                                    className="form-control"
                                                    id="inputDateBirth"
                                                    onChange={props.handleChange}
                                                    onBlur={props.handleBlur}
                                                />
                                            </div>

                                            <div className="form-group col-md-6">
                                                <label htmlFor="inputDrivingLicence">Driving licence</label>
                                                <input type="text"
                                                    name="drivingLicence"
                                                    value={props.drivingLicence}
                                                    className="form-control"
                                                    id="inputDrivingLicence"
                                                    placeholder="B"
                                                    onChange={props.handleChange}
                                                    onBlur={props.handleBlur}
                                                />
                                            </div>

                                        </div>
                                        <div className="form-row">
                                            <div className="form-group col-md-6">
                                                <label htmlFor="inputLinkedIn">LinkedIn</label>
                                                <input type="text"
                                                    name="linkedin"
                                                    value={props.linkedin}
                                                    className="form-control"
                                                    id="inputLinkedIn"
                                                    onChange={props.handleChange}
                                                    onBlur={props.handleBlur} />
                                            </div>

                                            <div className="form-group col-md-6">
                                                <label htmlFor="inputWebsite">Personal Website</label>
                                                <input type="text"
                                                    name="personalSite"
                                                    value={props.personalSite}
                                                    className="form-control"
                                                    id="inputWebsite"
                                                    onChange={props.handleChange}
                                                    onBlur={props.handleBlur}
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="toggler-additionalInfo">
                                        <a href="#/" className="btn-additionalInfo" onClick={() => setOpenAditionalInfo(!openAditionalInfo)}><i className={openAditionalInfo === false ? "fa fa-plus-circle" : "fa fa-minus-circle"} aria-hidden="true"></i> Additional information</a>
                                    </div>

                                    <hr className="hr" />

                                    <div className="btn-next">
                                        <input type="submit" name="submit" className="btn btn-primary" value="Next "/>
                                    </div>

                                </div>
                            </div>


                            {/**********************************Experience form********************************************************/}

                            <div className="experience" style={{
                                display: stateForm !== 2 && 'none'
                            }}>
                                <h3>Experiences</h3>
                                <hr className="hr" />
                                <div className="form-cls">
                                    {/* <div className="form-group avatar col-md-2">
                            <img id="avatar" src="./images/cap.png" alt="" />
                        </div> */}

                                    {/************************WORK**************************/}

                                    <h3 className="text-secondary" onClick={() => setWorkExperience(!workExperience)}><i className="fa fa-briefcase icon text-dark"></i>Work experience</h3>
                                    <hr className="hr" />
                                    <div className="work-section" style={{
                                        display: workExperience === false && 'none'
                                    }}>

                                        <div className="form-row">
                                            {notes.length === 0 ? null : (
                                                <div className="form-group col-md-12">
                                                    {notes.map((noteItem, index) => {
                                                        return (
                                                            <Note
                                                                key={index}
                                                                id={index}
                                                                title={noteItem.job_title}
                                                                start={noteItem.start}
                                                                end={noteItem.end}
                                                                onDelete={deleteNote}

                                                            />);
                                                    })}
                                                </div>
                                            )}
                                        </div>

                                        <div className="fields-work" style={{
                                            display: workFields === false && 'none'
                                        }}>
                                            <div className="form-row">
                                                <div className="form-group col-md-6">
                                                    <label htmlFor="nputJobTitle">Job title*</label>
                                                    <input type="text"
                                                        name="job_title"
                                                        onChange={handleChange}
                                                        value={work.job_title}
                                                        className="form-control"
                                                        id="inputJobTitle"
                                                        placeholder="" />
                                                </div>
                                                <div className="form-group col-md-6">
                                                    <label htmlFor="inputCity2">City*</label>
                                                    <input type="text"
                                                        name="city"
                                                        onChange={handleChange}
                                                        value={work.city}
                                                        className="form-control"
                                                        id="inputCity2" placeholder="" />
                                                </div>
                                            </div>
                                            <div className="form-row">
                                                <div className="form-group col-md-12">
                                                    <label htmlFor="inputCompany">Company*</label>
                                                    <input type="text"
                                                        name="company"
                                                        onChange={handleChange}
                                                        value={work.company}
                                                        className="form-control"
                                                        id="inputCompany" placeholder="" />
                                                </div>
                                            </div>
                                            <div className="form-row">
                                                <div className="form-group col-md-6">
                                                    <label htmlFor="inputStartDate">Start date*</label>
                                                    <input type="date"
                                                        name="start"
                                                        onChange={handleChange}
                                                        value={work.start}
                                                        className="form-control"
                                                        id="inputStartDate" placeholder="" />
                                                </div>

                                                <div className="form-group col-md-6">
                                                    <label htmlFor="inputEndDate">End date*</label>
                                                    <input type="date"
                                                        name="end"
                                                        onChange={handleChange}
                                                        value={work.end}
                                                        className="form-control"
                                                        id="inputEndDate" placeholder="" />
                                                </div>
                                            </div>

                                            <div className="form-row">
                                                <div className="form-group col-md-12">
                                                    <label htmlFor="inputDescription">Description</label>
                                                    <textarea name="descriere" onChange={handleChange}
                                                        value={work.descriere}
                                                        className="form-control"
                                                        id="inputDescription" placeholder="" />
                                                </div>
                                            </div>

                                            <div className="select-option">
                                                <button onClick={removeWork} className="btn"><i className="fas fa-trash-alt"></i>Delete</button>
                                                <button onClick={submitWork} className="btn"><i className="fas fa-save"></i>Save</button>
                                            </div>
                                        </div>
                                        <div className="toggler-additionalInfo">
                                            <a href="#/" className="btn-additionalInfo" onClick={() => { setWorkFields(true) }}><i className="fa fa-plus-circle" aria-hidden="true"></i> Add another work experience</a>
                                        </div>
                                        <hr className="hr" />
                                    </div>




                                    <div className="two-btn">
                                        <button className="btn btn-primary btn-prev" onClick={() => setStateForm(1)}><i className="fa fa-arrow-left" aria-hidden="true"> Previous</i></button>

                                        <button type="submit" className="btn btn-primary" onClick={() => setStateForm(3)}>Next  <i className="fa fa-arrow-right" aria-hidden="true"></i></button>
                                    </div>

                                </div>
                            </div>

                            {/**********************************Finish form********************************************************/}

                            <div className="finish" style={{
                                display: stateForm !== 3 && 'none'
                            }}>
                                <PDF />

                                <div className="two-btn">
                                    <button className="btn btn-primary btn-prev" onClick={() => setStateForm(2)}><i className="fa fa-arrow-left" aria-hidden="true"> Previous</i></button>

                                    <button type="submit" className="btn btn-primary">Export pdf</button>
                                </div>
                            </div>
                        </div>
                    </form>
                )}
            </Formik>
        </div>
    )
}

export default MakeCV
