import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { isEmail } from "validator";
import AuthService from "../services/auth.service";
import './Login.css';

function Login({ show, close }) {

    let history = useHistory();

    const [toggler, setToggler] = useState(false);
    const [full_name, setFull_name] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState("");
    const [successful, setSuccessful] = useState(true);


    const required = value => {
        if (!value) {
            return (
                <div className="alert alert-danger" role="alert">
                    This field is required!
                </div>
            );
        }
    };

    const vFull_name = value => {
        if (value.length < 3 || value.length > 100) {
            return (
                <div className="alert alert-danger" role="alert">
                    The full_name must be between 3 and 100 characters.
                </div>
            );
        }
    }

    const vEmail = value => {
        if (!isEmail(value)) {
            return (
                <div className="alert alert-danger" role="alert">
                    This is not a valid email.
                </div>
            );
        }
    }

    const vPassword = value => {
        if (value.length < 6 || value.length > 50) {
            return (
                <div className="alert alert-danger" role="alert">
                    The password must be between 6 and 50 characters.
                </div>
            );
        }
    }

    function onChangeFull_name(event) {
        setFull_name(event.target.value);
    }

    function onChangeEmail(event) {
        setEmail(event.target.value);
    }

    function onChangePassword(event) {
        setPassword(event.target.value);
    }


    function handleLogin(e) {
        e.preventDefault();

        AuthService.login(email, password).then(
            () => {
                history.push("/user");
                window.location.reload();
            },
            error => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();

                setMessage(resMessage);
            }
        )
    }

    function handleRegister(e) {
        e.preventDefault();

        AuthService.register(
            full_name,
            email,
            password
        ).then(
            response => {
                setMessage(response.data.message);
                setSuccessful(true);
            },
            error => {
                const resMessage = (
                    error.response &&
                    error.response.data &&
                    error.response.data.message) || error.message || error.toString();
                setMessage(resMessage);
                setSuccessful(false);
            }
        );

    }

    return (
        <div>
            <div className="back-drop" style={{
                transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
            }} onClick={close} />

            <div className="login" style={{
                display: toggler && 'none'
            }}>
                <h1>Login</h1>
                <form onSubmit={handleLogin}>
                    <div className="txt_field">
                        <input
                            type="text"
                            name="email"
                            value={email!="" && email}
                            onChange={onChangeEmail}
                            validations={[required]}
                        />
                        <label>Email</label>
                        <span></span>
                    </div>
                    <div className="txt_field">
                        <input
                            type="password"
                            name="password"
                            value={password!="" && password}
                            onChange={onChangePassword}
                            validations={[required]}
                        />
                           <label>Password</label>
                        <span></span>
                    </div>
                    <div className="pass">Forgot Password?</div>
                    <input type="submit" value="Login" />

                    <div className="auth"><span>OR</span></div>
                    <div className="facebook">
                        <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                    </div>
                </form>

                <div className="signup_link">
                    Dont't have an account? <a href="#" onClick={() => setToggler(true)}>Sign up</a>
                </div>

                {message && (
                    <div className="form-group container">
                        <div className="alert alert-danger" role="alert">
                            {message}
                        </div>
                    </div>
                )}
            </div>

            <div className="register" style={{
                display: toggler === false && 'none'
            }
            }>
                {successful && (
                    <div>
                        <h1>Register</h1>
                        <form onSubmit={handleRegister} >
                            <div className="txt_field">
                                <input
                                    type="text"
                                    name="full_name"
                                    value={full_name}
                                    onChange={onChangeFull_name}
                                    validations={[required, vFull_name]}
                                />
                                <label>Full name</label>
                                <span></span>
                            </div>
                            <div className="txt_field">

                                <input
                                    type="text"
                                    name="email"
                                    value={email}
                                    onChange={onChangeEmail}
                                    validations={[required, vEmail]}
                                />
                                <label>Email</label>
                                <span></span>
                            </div>
                            <div className="txt_field">

                                <input
                                    type="password"
                                    name="password"
                                    value={password}
                                    onChange={onChangePassword}
                                    validations={[required, vPassword]}
                                />
                                <label>Password</label>
                                <span></span>
                            </div>
                            <input type="submit" value="Register" />

                            <div className="auth"><span>OR</span></div>
                            <div className="facebook">
                                <i className="fab fa-facebook-square"><span>Log in with Facebook</span></i>
                            </div>
                        </form>

                        <div className="login_link">
                            Have an account? <a href="#" onClick={() => setToggler(false)}>Log in</a>
                        </div>
                    </div>
                )}

                {message && (
                    <div className="form-group">
                        <div
                            className={
                                successful
                                    ? "alert alert-success"
                                    : "alert alert-danger"
                            }
                            role="alert"
                        >
                            {message}
                        </div>
                    </div>
                )}
            </div>


        </div>
    )
}

export default Login
