import React from 'react'
import styled from 'styled-components';
import { MdClose } from 'react-icons/md'
import './Modal.css';

const Background = styled.div`
 width:100%;
 height:100%;
 background:rgba(0,0,0,0.8);
 position:fixed;
 display:flex;
 justify-content:center;
 align-items:center;
`;

const ModalWrapper = styled.div`
width:800px;
height:500px;
box-shadow:0 5px 16px rgba(0,0,0,0.2);
background:#fff;
color:#000;
display:grid;
grid-template-columns:1fr 1fr;
position:relative;
z-index:10;
border-radius:10px;
`;
const ModalImg = styled.img`
 width:100%;
 height:100%;
 border-radius:10px 0 0 10px;
 background:#000; 
`;

const ModalContent = styled.div`
display:flex;
flex-direction:column;
justify-content:center;
align-items:center;
line-height:1.8;
color:#141414;

p{
    margin-bottom:1rem;
}

button{
    padding:10px 24px;
    background:#141414;
    color:#fff;
    border:none;
}
`;

const CloseModalButton = styled(MdClose)`
cursor:pointer;
position:absolute;
top:20px;
right:20px;
width:32px;
height:32px;
padding:0px;
z-index:10px;
`;



export const Modal = ({ showModal, setShowModal }) => {
    return (
        <>
            {showModal ? (
                <div className="background">
                   <div className="modalWrapper" showModal={showModal}>
                        <img className="modalmg" src={require('../images/iconpng')} alt='camera' />
                        <div className="modalContent">
                            <h1>Are you ready?</h1>
                            <p>Get exclusive acces to our next lunch</p>
                            <button>Join now</button>
                        </div>
                        <button className="closeModalButton" aria-label='Close modal' onClick={()=>setShowModal(prev => !prev)} />
                      </div>
                    </div>
            ) : null}

        </>
    );
};
